mapper.update:
update `user_role` set `deleted` = 1

    and msg_id = #{msgId}
    and `msg_to` = #{msgTo}
    and `read_state` = #{readState}
    and `read_time` = #{readTime}

mapper.select:
select

from `user_role`

    and msg_id = #{msgId}
    and `msg_to` = #{msgTo}
    and `read_state` = #{readState}
    and `read_time` = #{readTime}
    and `deleted` = #{deleted}

limit 1

select

from `user_role`

    and msg_id = #{msgId}
    and `msg_to` = #{msgTo}
    and `read_state` = #{readState}
    and `read_time` = #{readTime}
    and `deleted` = #{deleted}
